const SkillsVisualization: React.FC = () => {
  const svgRef = useRef<SVGSVGElement>(null);
  const tooltipRef = useRef<HTMLDivElement>(null);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [hoveredSkill, setHoveredSkill] = useState<Skill | null>(null);
  const [focusedSkill, setFocusedSkill] = useState<Skill | null>(null);
  const [simulationRunning, setSimulationRunning] = useState<boolean>(true);

  const categories = Array.from(new Set(skills.map(skill => skill.category)));

  // Category color mapping
  const categoryColors: Record<string, string> = {
    'Programming': '#64FFDA',
    'Data Analysis': '#FF6B6B',
    'Web Development': '#48BEFF',
    'Machine Learning': '#9D8DF1',
    'Data Visualization': '#FFD166'
  };

  useEffect(() => {
    if (!svgRef.current) return;

    const filteredSkills = selectedCategory === 'all' 
      ? skills 
      : skills.filter(skill => skill.category === selectedCategory);

    const width = Math.min(900, window.innerWidth - 40);
    const height = Math.min(600, window.innerHeight - 200);

    // Clear previous visualization
    d3.select(svgRef.current).selectAll('*').remove();

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height)
      .attr('viewBox', [0, 0, width, height])
      .attr('style', 'max-width: 100%; height: auto;');

    // Create a container for the nodes
    const container = svg.append('g')
      .attr('transform', `translate(${width / 2},${height / 2})`);

    // Add a subtle grid pattern
    const defs = svg.append('defs');
    const pattern = defs.append('pattern')
      .attr('id', 'grid')
      .attr('width', 20)
      .attr('height', 20)
      .attr('patternUnits', 'userSpaceOnUse')
      .attr('patternTransform', 'rotate(45)');

    pattern.append('rect')
      .attr('width', 20)
      .attr('height', 20)
      .attr('fill', 'none');

    pattern.append('path')
      .attr('d', 'M 0,0 L 0,20 20,20 20,0 Z')
      .attr('fill', 'none')
      .attr('stroke', 'rgba(100, 255, 218, 0.05)')
      .attr('stroke-width', 0.5);

    // Add the background
    svg.append('rect')
      .attr('width', width)
      .attr('height', height)
      .attr('fill', 'url(#grid)');

    // Add circular gradients for nodes
    filteredSkills.forEach((skill, i) => {
      const color = categoryColors[skill.category] || '#64FFDA';

      // Create gradient for each node
      const nodeGradient = defs.append('radialGradient')
        .attr('id', `node-gradient-${i}`)
        .attr('cx', '30%')
        .attr('cy', '30%');

      nodeGradient.append('stop')
        .attr('offset', '0%')
        .attr('stop-color', 'white')
        .attr('stop-opacity', 0.9);

      nodeGradient.append('stop')
        .attr('offset', '100%')
        .attr('stop-color', color)
        .attr('stop-opacity', 0.7);
    });

    // Create a scale for node radius based on skill level
    const radiusScale = d3.scaleSqrt()
      .domain([70, 100])  // Minimum and maximum skill levels
      .range([30, 60]);   // Minimum and maximum radius in pixels

    // Calculate initial positions in a spiral pattern
    const nodeData = filteredSkills.map((skill, i) => {
      const angle = (i / filteredSkills.length) * 2 * Math.PI;
      const radius = Math.min(width, height) * 0.3;
      return {
        ...skill,
        x: Math.cos(angle) * radius,
        y: Math.sin(angle) * radius,
        radius: radiusScale(skill.level)
      };
    });

    // Create force simulation
    const simulation = d3.forceSimulation(nodeData)
      .force('charge', d3.forceManyBody().strength(10))  // Slight attraction
      .force('collide', d3.forceCollide().radius((d: any) => d.radius + 5).strength(0.8))
      .force('center', d3.forceCenter(0, 0).strength(0.05))
      // Optional forces to create more interesting dynamics
      .force('x', d3.forceX().strength(0.02))
      .force('y', d3.forceY().strength(0.02));

    // Create groups for nodes (for better interaction)
    const nodes = container.selectAll('.node')
      .data(nodeData)
      .enter()
      .append('g')
      .attr('class', 'node cursor-pointer')
      .attr('transform', (d: any) => `translate(${d.x},${d.y})`)
      .call(d3.drag<SVGGElement, any>()
        .on('start', (event, d) => {
          if (!event.active) simulation.alphaTarget(0.3).restart();
          d.fx = d.x;
          d.fy = d.y;
        })
        .on('drag', (event, d) => {
          d.fx = event.x;
          d.fy = event.y;
        })
        .on('end', (event, d) => {
          if (!event.active) simulation.alphaTarget(0);
          d.fx = null;
          d.fy = null;
        })
      );

    // Add the circles for each node
    nodes.append('circle')
      .attr('r', (d: any) => d.radius)
      .attr('fill', (d: any, i) => `url(#node-gradient-${i})`)
      .attr('stroke', (d: any) => categoryColors[d.category] || '#64FFDA')
      .attr('stroke-width', 2)
      .attr('class', 'transition-all duration-300')
      .style('filter', 'drop-shadow(0 0 10px rgba(0, 0, 0, 0.3))')
      .on('mouseover', (event, d: any) => {
        setHoveredSkill(d);
        if (tooltipRef.current) {
          tooltipRef.current.style.opacity = '1';
          tooltipRef.current.style.left = event.pageX + 10 + 'px';
          tooltipRef.current.style.top = event.pageY - 10 + 'px';
        }

        // Highlight the hovered node
        d3.select(event.currentTarget)
          .transition()
          .duration(200)
          .attr('stroke-width', 3)
          .style('filter', 'drop-shadow(0 0 15px rgba(100, 255, 218, 0.5))');
      })
      .on('mouseout', (event) => {
        setHoveredSkill(null);
        if (tooltipRef.current) {
          tooltipRef.current.style.opacity = '0';
        }

        // Reset the node style
        d3.select(event.currentTarget)
          .transition()
          .duration(200)
          .attr('stroke-width', 2)
          .style('filter', 'drop-shadow(0 0 10px rgba(0, 0, 0, 0.3))');
      })
      .on('click', (event, d: any) => {
        setFocusedSkill(focusedSkill === d ? null : d);

        // Update simulation to focus or release focus
        if (focusedSkill !== d) {
          nodeData.forEach((node: any) => {
            node.fx = null;
            node.fy = null;
          });

          // Fix the position of the clicked node
          d.fx = 0;
          d.fy = 0;

          simulation.alpha(0.3).restart();
        } else {
          d.fx = null;
          d.fy = null;
          simulation.alpha(0.3).restart();
        }
      });

    // Add text for larger nodes
    nodes.append('text')
      .attr('text-anchor', 'middle')
      .attr('dominant-baseline', 'middle')
      .attr('class', 'fill-primary font-medium pointer-events-none select-none')
      .style('font-size', (d: any) => `${Math.max(10, d.radius * 0.3)}px`)
      .text((d: any) => d.name);

    // Add small indicators for skill level
    nodes.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', (d: any) => d.radius / 3)
      .attr('class', 'fill-primary text-xs font-mono pointer-events-none select-none')
      .text((d: any) => `${d.level}%`);

    // Add category indicators as small badges
    nodes.append('text')
      .attr('text-anchor', 'middle')
      .attr('y', (d: any) => -d.radius / 2.5)
      .attr('class', 'fill-primary text-[10px] pointer-events-none select-none')
      .style('font-style', 'italic')
      .text((d: any) => d.category);

    // Add progress arc to represent skill level
    nodes.append('path')
      .attr('d', (d: any) => {
        const arcGenerator = d3.arc()
          .innerRadius(d.radius - 3)
          .outerRadius(d.radius)
          .startAngle(0)
          .endAngle(2 * Math.PI * (d.level / 100));

        return arcGenerator();
      })
      .attr('fill', (d: any) => categoryColors[d.category] || '#64FFDA')
      .attr('opacity', 0.8);

    // Update node positions on each tick of the simulation
    simulation.on('tick', () => {
      nodes.attr('transform', (d: any) => `translate(${d.x},${d.y})`);
    });

    // Controls for simulation
    const toggleSimulation = () => {
      setSimulationRunning(!simulationRunning);

      if (simulationRunning) {
        simulation.stop();
      } else {
        simulation.restart();
      }
    };

    // Add play/pause button
    const buttonGroup = svg.append('g')
      .attr('transform', `translate(${width - 50}, ${height - 50})`)
      .attr('class', 'cursor-pointer')
      .on('click', toggleSimulation);

    buttonGroup.append('circle')
      .attr('r', 20)
      .attr('fill', 'rgba(100, 255, 218, 0.2)')
      .attr('stroke', 'rgba(100, 255, 218, 0.8)')
      .attr('stroke-width', 1.5);

    buttonGroup.append('path')
      .attr('d', simulationRunning ? 
        'M10,-7 L10,7 M-10,-7 L-10,7' : // pause icon
        'M-7,-10 L13,0 L-7,10 Z'        // play icon
      )
      .attr('fill', simulationRunning ? 'none' : 'rgba(100, 255, 218, 0.8)')
      .attr('stroke', 'rgba(100, 255, 218, 0.8)')
      .attr('stroke-width', 2);

    // Cleanup function
    return () => {
      simulation.stop();
    };
  }, [selectedCategory, focusedSkill, categoryColors, simulationRunning]);

  // Get color for a category
  const getCategoryColor = (category: string) => {
    return categoryColors[category] || '#64FFDA';
  };

  return (
    <section id="skills" className="py-20 relative overflow-hidden">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="container mx-auto px-4"
      >
        <h2 className="text-4xl font-heading font-bold text-secondary mb-8">Skills & Expertise</h2>

        <div className="mb-8">
          <p className="text-neutral-light mb-6 max-w-3xl">
            Explore my technical skills through this interactive visualization. Drag the nodes to interact with them, click on a skill to learn more, or filter by category to focus on specific areas of expertise.
          </p>

          <div className="flex flex-wrap gap-3 mb-8">
            <motion.button
              onClick={() => setSelectedCategory('all')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`px-4 py-2 rounded-md transition-all duration-300 ${
                selectedCategory === 'all' 
                  ? 'bg-secondary text-primary shadow-lg shadow-secondary/20' 
                  : 'border border-secondary text-secondary hover:bg-secondary/10'
              }`}
            >
              All Skills
            </motion.button>
            {categories.map(category => (
              <motion.button
                key={category}
                onClick={() => setSelectedCategory(category)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`px-4 py-2 rounded-md transition-all duration-300 flex items-center gap-2 ${
                  selectedCategory === category 
                    ? 'bg-secondary text-primary shadow-lg shadow-secondary/20' 
                    : 'border border-none text-neutral-light hover:text-secondary'
                }`}
                style={{
                  backgroundColor: selectedCategory === category ? getCategoryColor(category) : 'transparent',
                  borderColor: getCategoryColor(category)
                }}
              >
                <span 
                  className="w-3 h-3 rounded-full" 
                  style={{ backgroundColor: getCategoryColor(category) }}
                />
                {category}
              </motion.button>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 relative w-full bg-neutral/5 rounded-xl overflow-hidden border border-neutral/10 shadow-inner">
            <div className="flex justify-center p-4 h-[600px]">
              <svg ref={svgRef} className="w-full h-full" />
            </div>

            <div className="absolute bottom-4 left-4 bg-primary/80 backdrop-blur-sm px-4 py-2 rounded-lg text-neutral text-xs border border-neutral/10">
              <p>🖱️ <span className="text-neutral-light">Drag</span> to move skills</p>
              <p>👆 <span className="text-neutral-light">Click</span> on a skill to focus</p>
            </div>

            <motion.div
              ref={tooltipRef}
              initial={{ opacity: 0 }}
              animate={{ opacity: hoveredSkill ? 1 : 0 }}
              className="fixed pointer-events-none bg-primary/90 backdrop-blur-sm text-neutral-light px-4 py-2 rounded-md border border-secondary/20 shadow-lg shadow-secondary/10 z-50 min-w-[200px]"
            >
              {hoveredSkill && (
                <div>
                  <p className="font-medium text-secondary text-lg">{hoveredSkill.name}</p>
                  <div 
                    className="flex items-center gap-2 my-1"
                    style={{ color: getCategoryColor(hoveredSkill.category) }}
                  >
                    <span className="w-2 h-2 rounded-full" style={{ backgroundColor: getCategoryColor(hoveredSkill.category) }} />
                    <span className="text-sm">{hoveredSkill.category}</span>
                  </div>
                  <div className="w-full bg-neutral/20 h-1.5 rounded-full mt-2">
                    <div 
                      className="h-full rounded-full" 
                      style={{ 
                        width: `${hoveredSkill.level}%`,
                        backgroundColor: getCategoryColor(hoveredSkill.category)
                      }} 
                    />
                  </div>
                  <p className="text-xs font-mono mt-1">Proficiency: {hoveredSkill.level}%</p>
                  {hoveredSkill.description && (
                    <p className="text-sm mt-2 text-neutral-light">{hoveredSkill.description}</p>
                  )}
                </div>
              )}
            </motion.div>
          </div>

          <div className="bg-neutral/5 rounded-xl p-6 border border-neutral/10">
            <h3 className="text-xl font-heading font-bold text-secondary mb-4">Skills Breakdown</h3>

            <div className="space-y-4">
              {categories.map(category => (
                <motion.div
                  key={category}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className="space-y-2"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      <span 
                        className="w-3 h-3 rounded-full" 
                        style={{ backgroundColor: getCategoryColor(category) }}
                      />
                      <h4 className="font-medium text-neutral-light">{category}</h4>
                    </div>
                    <span className="text-xs text-neutral">
                      {skills.filter(s => s.category === category).length} skills
                    </span>
                  </div>

                  <div className="space-y-2">
                    {skills
                      .filter(skill => skill.category === category)
                      .sort((a, b) => b.level - a.level)
                      .map(skill => (
                        <div key={skill.name} className="flex justify-between items-center">
                          <span className="text-sm text-neutral-light">{skill.name}</span>
                          <div className="flex items-center gap-2">
                            <div className="w-24 bg-neutral/20 h-1 rounded-full">
                              <div 
                                className="h-full rounded-full" 
                                style={{ 
                                  width: `${skill.level}%`,
                                  backgroundColor: getCategoryColor(category)
                                }} 
                              />
                            </div>
                            <span className="text-xs text-neutral">{skill.level}%</span>
                          </div>
                        </div>
                      ))
                    }
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </div>
      </motion.div>
    </section>
  );
};

export default SkillsVisualization;
