import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import { motion } from 'framer-motion';

interface Skill {
  name: string;
  level: number;
  category: string;
}

const skills: Skill[] = [
  // Programming Languages
  { name: 'Python', level: 100, category: 'Programming' },
  { name: 'SQL', level: 100, category: 'Programming' },
  { name: 'PySpark', level: 80, category: 'Programming' },

  // Data Analysis
  { name: 'Power BI', level: 95, category: 'Data Analysis' },
  { name: 'DAX', level: 90, category: 'Data Analysis' },
  { name: 'Data Modeling', level: 85, category: 'Data Analysis' },
  { name: 'Z-Score', level: 80, category: 'Data Analysis' },

  // Web Development
  { name: 'React.js', level: 85, category: 'Web Development' },
  { name: 'Node.js', level: 80, category: 'Web Development' },
  { name: 'MongoDB', level: 75, category: 'Web Development' },
  { name: 'Express.js', level: 80, category: 'Web Development' },

  // Machine Learning
  { name: 'Keras', level: 75, category: 'Machine Learning' },
  { name: 'Scikit-learn', level: 80, category: 'Machine Learning' },

  // Data Visualization
  { name: 'Plotly', level: 85, category: 'Data Visualization' },
  { name: 'Seaborn', level: 80, category: 'Data Visualization' },
  { name: 'Matplotlib', level: 95, category: 'Data Visualization' },
];

const SkillsVisualization: React.FC = () => {
  const svgRef = useRef<SVGSVGElement>(null);
  const tooltipRef = useRef<HTMLDivElement>(null);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [hoveredSkill, setHoveredSkill] = useState<Skill | null>(null);

  const categories = Array.from(new Set(skills.map(skill => skill.category)));

  useEffect(() => {
    if (!svgRef.current) return;

    const filteredSkills = selectedCategory === 'all' 
      ? skills 
      : skills.filter(skill => skill.category === selectedCategory);

    const margin = { top: 50, right: 100, bottom: 50, left: 100 };
    const width = Math.min(800, window.innerWidth - 40);
    const height = width;
    const radius = Math.min(width, height) / 2 - Math.max(...Object.values(margin));

    // Clear previous visualization
    d3.select(svgRef.current).selectAll('*').remove();

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2},${height / 2})`);

    // Create scales
    const angleScale = d3.scaleLinear()
      .domain([0, filteredSkills.length])
      .range([0, 2 * Math.PI]);

    const radiusScale = d3.scaleLinear()
      .domain([0, 100])
      .range([0, radius]);

    // Create radar path generator
    const line = d3.lineRadial<Skill>()
      .angle((d, i) => angleScale(i))
      .radius(d => radiusScale(d.level))
      .curve(d3.curveCardinalClosed.tension(0.5));

    // Draw background circles with gradient
    const circles = [20, 40, 60, 80, 100];
    const gradient = svg.append('defs')
      .append('radialGradient')
      .attr('id', 'skill-gradient')
      .attr('gradientUnits', 'userSpaceOnUse')
      .attr('cx', 0)
      .attr('cy', 0)
      .attr('r', radius);

    gradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', 'rgb(100, 255, 218)')
      .attr('stop-opacity', 0.1);

    gradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', 'rgb(100, 255, 218)')
      .attr('stop-opacity', 0);

    svg.append('circle')
      .attr('r', radius)
      .attr('fill', 'url(#skill-gradient)');

    circles.forEach(value => {
      svg.append('circle')
        .attr('cx', 0)
        .attr('cy', 0)
        .attr('r', radiusScale(value))
        .attr('class', 'fill-none stroke-neutral/20');

      svg.append('text')
        .attr('x', 5)
        .attr('y', -radiusScale(value))
        .attr('class', 'fill-neutral text-xs')
        .text(value.toString());
    });

    // Draw skills path with gradient fill
    const skillGradient = svg.append('defs')
      .append('linearGradient')
      .attr('id', 'area-gradient')
      .attr('gradientUnits', 'userSpaceOnUse')
      .attr('x1', 0)
      .attr('y1', -radius)
      .attr('x2', 0)
      .attr('y2', radius);

    skillGradient.append('stop')
      .attr('offset', '0%')
      .attr('stop-color', 'rgb(100, 255, 218)')
      .attr('stop-opacity', 0.3);

    skillGradient.append('stop')
      .attr('offset', '100%')
      .attr('stop-color', 'rgb(100, 255, 218)')
      .attr('stop-opacity', 0.1);

    // Draw skills area
    svg.append('path')
      .datum(filteredSkills)
      .attr('class', 'fill-secondary/20 stroke-secondary transition-all duration-300')
      .attr('d', line as any)
      .style('filter', 'drop-shadow(0 0 10px rgba(100, 255, 218, 0.2))');

    // Draw skill points and labels
    filteredSkills.forEach((skill, i) => {
      const angle = angleScale(i);
      const x = radiusScale(skill.level) * Math.sin(angle);
      const y = -radiusScale(skill.level) * Math.cos(angle);

      // Draw connecting line
      svg.append('line')
        .attr('x1', 0)
        .attr('y1', 0)
        .attr('x2', x)
        .attr('y2', y)
        .attr('class', 'stroke-secondary/20');

      // Draw skill point
      svg.append('circle')
        .attr('cx', x)
        .attr('cy', y)
        .attr('r', 4)
        .attr('class', 'fill-secondary transition-all duration-300 cursor-pointer')
        .on('mouseover', (event) => {
          setHoveredSkill(skill);
          if (tooltipRef.current) {
            tooltipRef.current.style.opacity = '1';
            tooltipRef.current.style.left = event.pageX + 10 + 'px';
            tooltipRef.current.style.top = event.pageY - 10 + 'px';
          }
        })
        .on('mouseout', () => {
          setHoveredSkill(null);
          if (tooltipRef.current) {
            tooltipRef.current.style.opacity = '0';
          }
        });

      // Add skill labels
      const labelRadius = radius + 20;
      const labelX = labelRadius * Math.sin(angle);
      const labelY = -labelRadius * Math.cos(angle);

      svg.append('text')
        .attr('x', labelX)
        .attr('y', labelY)
        .attr('text-anchor', labelX > 0 ? 'start' : 'end')
        .attr('class', 'fill-neutral-light text-sm transition-all duration-300')
        .attr('transform', `rotate(${angle * 180 / Math.PI - 90}, ${labelX}, ${labelY})`)
        .text(skill.name);
    });

  }, [selectedCategory]);

  return (
    <section id="skills" className="py-20 relative overflow-hidden">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="container mx-auto px-4"
      >
        <h2 className="text-4xl font-heading font-bold text-secondary mb-8">Skills & Expertise</h2>

        <div className="mb-8 flex flex-wrap gap-4">
          <motion.button
            onClick={() => setSelectedCategory('all')}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className={`px-4 py-2 rounded-md transition-all duration-300 ${
              selectedCategory === 'all' 
                ? 'bg-secondary text-primary shadow-lg shadow-secondary/20' 
                : 'border border-secondary text-secondary hover:bg-secondary/10'
            }`}
          >
            All Skills
          </motion.button>
          {categories.map(category => (
            <motion.button
              key={category}
              onClick={() => setSelectedCategory(category)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`px-4 py-2 rounded-md transition-all duration-300 ${
                selectedCategory === category 
                  ? 'bg-secondary text-primary shadow-lg shadow-secondary/20' 
                  : 'border border-secondary text-secondary hover:bg-secondary/10'
              }`}
            >
              {category}
            </motion.button>
          ))}
        </div>

        <div className="relative w-full overflow-x-auto">
          <div className="flex justify-center">
            <svg ref={svgRef} className="max-w-full" />
          </div>
          <motion.div
            ref={tooltipRef}
            initial={{ opacity: 0 }}
            animate={{ opacity: hoveredSkill ? 1 : 0 }}
            className="fixed pointer-events-none bg-primary/90 backdrop-blur-sm text-neutral-light px-4 py-2 rounded-md border border-secondary/20 shadow-lg shadow-secondary/10 z-50"
          >
            {hoveredSkill && (
              <div>
                <p className="font-medium text-secondary">{hoveredSkill.name}</p>
                <p className="text-sm">{hoveredSkill.category}</p>
                <p className="text-sm font-mono">Proficiency: {hoveredSkill.level}%</p>
              </div>
            )}
          </motion.div>
        </div>
      </motion.div>
    </section>
  );
};

export default SkillsVisualization;
